SELECT o.tms_order_id AS tmsOrderId, o.waybill_no AS waybillNo, o.tracking_no AS trackingNo, o.customer_account AS customerAccount, o.depot_id AS depotId, o.shipping_status AS shippingStatus,o.site_id AS siteId,o.customer_order_id  as customerOrderId,o.shipping_id as shippingId


ORDER BY o.gmt_created DESC

  limit #{offset}, #{max}

SELECT a.customer_order_id as customerOrderId,b.consignee_country as country, b.consignee_country_name as countryName,c.shipping_id as shippingId, c.shipping_name as shippingName,d.telphone as telephone,e.track_link as trackLink,f.package_num as packageNum FROM tms_order_info a LEFT JOIN tms_order_user_info b ON a.tms_order_id = b.tms_order_id LEFT JOIN tms_shipping c ON a.shipping_id = c.shipping_id LEFT JOIN tms_shipping_country_telphone d ON a.shipping_id = d.shipping_id LEFT JOIN tms_carrier e ON c.carrier_terminal_delivery_id = e.carrier_id LEFT JOIN tms_order_package f ON a.tms_order_id = f.tms_order_id WHERE d.country_id = b.consignee_country
AND a.shipping_id = #{shippingId}
AND a.customer_order_id = #{orderId} limit 1

SELECT a.customer_order_id as customerOrderId,b.consignee_country as country, b.consignee_country_name as countryName,a.shipping_id as shippingId, f.package_num as packageNum FROM tms_order_info a LEFT JOIN tms_order_user_info b ON a.tms_order_id = b.tms_order_id LEFT JOIN tms_order_package f ON a.tms_order_id = f.tms_order_id WHERE a.customer_order_id = #{orderId}
AND a.shipping_id = #{shippingId}
limit 1

select o.customer_order_id as customerOrderId,o.tracking_no as shippingNo, o.customer_order_sn as customerOrderSn, o.route_code as routeCode, o. order_type as orderType, u.consignee_country as consigneeCountry
from tms_order_info o,
tms_order_user_info u
where o.tms_order_id = u.tms_order_id and
(o.tracking_no, o.customer_order_id)  in

  #{item.key}, #{item.value}

SELECT customer_order_id from tms_order_info
ORDER BY tms_order_id
limit #{offset}, #{max}
